import React, { useState } from 'react'
import './App.css'

function App() {
  const [form ,setForm] = useState ({})


  const handleForm = (e) => {
     console.log(e.target.value, e.target.name);
     setForm({
      [e.target.name] : e.target.value
     })
  }




  return (
    <div  >
      <p>{JSON.stringify(form)}</p>
      <form>
        {form}
        <span>Username</span>
        <input type='text' placeholder='enter' name='username' onChange={handleForm}></input>
         <span>password</span>
        <input type='text' placeholder='enter' name='password' onChange={handleForm}></input>
        <input type = "submit" className='btn btn-success'></input>
      </form>
    </div>
  )
}

export default App
